package ltd.fdsa.ds.api.bsdor;import lombok.var;import java.nio.charset.StandardCharsets;public class BoolItem implements Item<Boolean> {    private final boolean value;    private final Type type;    public BoolItem(boolean value) {        this.value = value;        if (value) {            this.type = Type.True;        } else {            this.type = Type.False;        }    }    @Override    public byte[] toByteArray() {        var v = (Short) this.type.getValue();        return new byte[]{v.byteValue()};    }    @Override    public Boolean getValue() {        return this.value;    }    @Override    public Type getType() {        return this.type;    }}