package ltd.fdsa.ds.api.bsdor;import lombok.var;import java.math.BigDecimal;import java.nio.ByteBuffer;/* * 64 位整型 (4294967295 到 18446744073709551615) * */public class DecimalItem implements Item<BigDecimal> {    private final BigDecimal value;    public DecimalItem(BigDecimal value) {        this.value = value;    }    @Override    public byte[] toByteArray() {        var data = this.value.toBigInteger().toByteArray();        ByteBuffer buffer = ByteBuffer.allocate(data.length + 9);        buffer.put(this.getType().getValue().byteValue());        buffer.putInt(data.length);        buffer.putInt(this.value.scale());        buffer.put(data);        return buffer.array();    }    @Override    public BigDecimal getValue() {        return this.value;    }    @Override    public Type getType() {        return Type.DECIMAL;    }}