package ltd.fdsa.ds.api.bsdor;import lombok.var;import java.nio.ByteBuffer;/* * 16 位整型 (256 到 65535) * */public class Number16Item implements Item<Short> {    private final Integer value;    private final Type type;    public Number16Item(int value) {        if (value > 0xFFFF || value < -0xFFFF) {            throw new IllegalArgumentException();        }        if (value > 0) {            this.type = Type.P_NUM8;        } else {            this.type = Type.N_NUM8;        }        this.value = value;    }    @Override    public byte[] toByteArray() {        ByteBuffer buffer = ByteBuffer.allocate(3);        buffer.put(this.type.getValue().byteValue());        var data = (this.value & 0xFFFF);        buffer.putShort((short) data);        return buffer.array();    }    @Override    public Short getValue() {        return this.value.shortValue();    }    @Override    public Type getType() {        return this.type;    }}