package ltd.fdsa.ds.api.bsdor;import lombok.var;import java.nio.ByteBuffer;/* * 32 位整型 (65535  到 4294967295 0xFFFFFFFF) * */public class Number32Item implements Item<Long> {    private final Long value;    private final Type type;    public Number32Item(long value) {        if (value > 0xFFFFFFFF || value < -0xFFFFFFFF) {            throw new IllegalArgumentException();        }        if (value > 0) {            this.type = Type.P_NUM16;        } else {            this.type = Type.N_NUM16;        }        this.value = value;    }    @Override    public byte[] toByteArray() {        ByteBuffer buffer = ByteBuffer.allocate(5);        buffer.put(this.type.getValue().byteValue());        var data = (this.value & 0xFFFFFFFF);        buffer.putShort((short) data);        return buffer.array();    }    @Override    public Long getValue() {        return this.value.longValue();    }    @Override    public Type getType() {        return this.type;    }}