package ltd.fdsa.ds.api.bsdor;import java.math.BigInteger;import java.nio.ByteBuffer;/* * 64 位整型 (4294967295 到 18446744073709551615) * */public class Number64Item implements Item<BigInteger> {    private final BigInteger value;    private final Type type;    public Number64Item(BigInteger value) {        if (value.signum() > 0) {            this.type = Type.P_NUM64;        } else {            this.type = Type.N_NUM64;        }        this.value = value;    }    public Number64Item(byte[] value) {        this(new BigInteger(value));    }    @Override    public byte[] toByteArray() {        ByteBuffer buffer = ByteBuffer.allocate(9);        buffer.put(this.type.getValue().byteValue());        buffer.put(this.value.toByteArray());        return buffer.array();    }    @Override    public BigInteger getValue() {        return this.value;    }    @Override    public Type getType() {        return this.type;    }}